export class InventoryItem {
    // uid = null;  // user id is generated by firebase (primar key)
    email = null;
    name = null;
    quantity = null;
    timestamp = null;
    docId = null; // doucument id generated by Firestore

    constructor(data) {
        // this.uid =data.uid;
        this.name = data.name.toLowerCase();
        this.quantity = data.quantity;
        this.timestamp = data.timestamp;
        this.email = data.email;
    }

    set_docId(docId) {
        this.docId = docId;
    }

    toFirestore(){
        return{
            email: this.email,
            name: this.name.toLowerCase(),
            quantity:this.quantity,
            timestamp: this.timestamp,
        };
    }
}
